package Keyword;

public class Frloops4 {

void display() {
		
		for(int i=1;i<=5;i++) {
			
			
			for(int j=1;j<=i;j++) {
				
				
				System.out.print("*");
			}
			System.out.println("");
				
		}
		
		
}

	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Frloops4 a2 = new Frloops4();
		a2.display();

	}

}
